import React, { useState } from 'react';

const MyForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [terms, setTerms] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email || !phone || !terms) {
      setMessage('Please fill in all fields and approve the terms and conditions.');
      return;
    }

    const phoneRegex = /^07\d{8}$/;
    if (!phoneRegex.test(phone)) {
      setMessage('Please enter a valid Romanian phone number.');
      return;
    }

    setMessage('The form has been registered!');
    setName('');
    setEmail('');
    setPhone('');
    setTerms(false);
  };

  return (
    <div style={{ textAlign: 'center', color: 'white', backgroundColor: 'black', padding: '20px' }}>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            placeholder="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ display: 'block', margin: '10px auto', padding: '10px', width: '80%', borderRadius: '5px', border: '1px solid white', backgroundColor: 'black', color: 'white' }}
          />
        </div>
        <div>
          <input
            type="email"
            placeholder="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ display: 'block', margin: '10px auto', padding: '10px', width: '80%', borderRadius: '5px', border: '1px solid white', backgroundColor: 'black', color: 'white' }}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            style={{ display: 'block', margin: '10px auto', padding: '10px', width: '80%', borderRadius: '5px', border: '1px solid white', backgroundColor: 'black', color: 'white' }}
          />
        </div>
        <div style={{ margin: '10px auto' }}>
          <input
            type="checkbox"
            checked={terms}
            onChange={(e) => setTerms(e.target.checked)}
            style={{ marginRight: '10px' }}
          />
          <label>Approve terms and conditions</label>
        </div>
        <button
          type="submit" id="submitbtn"
          style={
            { padding: '10px 20px',
              borderRadius: '5px',
              border: '1px solid white', 
              backgroundColor: 'black', 
              color: 'white', 
              cursor: 'pointer'}}>
          CLICK ME
        </button>
      </form>
      {message && <p style={{ marginTop: '20px', color: 'white' }}>{message}</p>}
    </div>
  );
};

export default MyForm;
