import React, { useState } from 'react';

const MyForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [terms, setTerms] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name) {
      setMessage('Please fill in your name');
      return;
    }

    if(!email){
      setMessage("Please fill in your email address");
      return;
    }

    if(!phone) {
      setMessage("Please fill in your phone number");
      return;
    }

    if(!terms) {
      setMessage("Please accept the terms and conditions");
      return;
    }

    const phoneRegex = /^07\d{8}$/; //linia asta a fost cautata pe net
    if (!phoneRegex.test(phone)) {
      setMessage('Please enter a valid phone number.');
      return;
    }

    setMessage('The form has been registered!');
    setName('');
    setEmail('');
    setPhone('');
    setTerms(false);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="nameField">
          <input
            type="text"
            placeholder="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="emailField">
          <input
            type="email"
            placeholder="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          </div>

        <div className="phoneField">
          <input
            type="text"
            placeholder="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div className = "checkTerms">
          <input
            type="checkbox"
            checked={terms}
            onChange={(e) => setTerms(e.target.checked)}
          />
          <label>Approve terms and conditions</label>
        </div>
        <button type="submit" id="submitbtn">CLICK ME </button>
      </form>
      {message && <p style={{ marginTop: '20px', color: 'white' }}>{message}</p>}
    </div>
  );
};

export default MyForm;
